import React, { useState } from "react";
import DoneAllIcon from "@mui/icons-material/DoneAll";
import CloseIcon from "@mui/icons-material/Close";

function Edit(props) {
  // const [title, setTitle] = useState(props.title);
  // const [content, setContent] = useState(props.content);

  function handleEditBtn() {
    props.onEditBtn(props.id, props.title, props.content);
  }



  function handleCloseBtn() {
    props.onDeleteBtn(props.id);
  }

  return (
    <div className="over-lay">
      <div className="edit-window">
        <h1>Edit Note</h1>

        <form className="edit-note">
          <input
            value={props.title}
            name="titles"
            placeholder="Title"
            onChange={(e) => setTitle(e.target.value)}
          />

          <textarea
            value={props.content}
            name="contents"
            placeholder="Take a note..."
            rows="3"
            onChange={(e) => setContent(e.target.value)}
          />
        </form>

        {/* edit button  */}
        <button className="edit-btn edit" onClick={handleEditBtn}>
          <DoneAllIcon />
        </button>

        {/* close button  */}
        <button className="edit-btn close" onClick={handleCloseBtn}>
          <CloseIcon />
        </button>
      </div>
    </div>
  );
}

export default Edit;
